/*Table structure for table `estimate` */

DROP TABLE IF EXISTS `estimate`;

CREATE TABLE `estimate` (
  `estimate_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(20) NOT NULL,
  `seed_name` varchar(20) NOT NULL,
  `fertilizer_cost` double(10,2) NOT NULL,
  `pesticide_cost` double(10,2) NOT NULL,
  `seed_cost` double(10,2) NOT NULL,
  `acres` double(10,2) NOT NULL,
  `cost` double(10,2) NOT NULL,
  `income` double(10,2) NOT NULL,
  PRIMARY KEY (`estimate_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

