/*Table structure for table `fertilizer` */

DROP TABLE IF EXISTS `fertilizer`;

CREATE TABLE `fertilizer` (
  `fertilizer_id` int(11) NOT NULL AUTO_INCREMENT,
  `fertilizer_name` varchar(20) NOT NULL,
  `fertilizer_type` int(10) NOT NULL COMMENT '0表示无机肥，1代表有机肥',
  `fertilizer_n` varchar(10) NOT NULL,
  `fertilizer_p` varchar(10) NOT NULL,
  `fertilizer_k` varchar(10) NOT NULL,
  `fertilizer_other` varchar(50) NOT NULL,
  `fertilizer_instructions` varchar(50) NOT NULL,
  `fertilizer_price` double(10,2) NOT NULL,
  `fertilizer_manufacturer` varchar(20) NOT NULL,
  `fertilizer_phone` varchar(20) NOT NULL,
  `fertilizer_address` varchar(50) NOT NULL,
  `fertilizer_productiondate` date NOT NULL,
  `fertilizer_shelflife` int(11) NOT NULL,
  `fertilizer_moderatecrop` varchar(50) NOT NULL,
  `recommend_data` date NOT NULL,
  PRIMARY KEY (`fertilizer_id`) USING BTREE
